INSERT INTO azkar (category, arabic_text, description, repetitions, reference, search_tags) VALUES
('دعاء لقاء العدو و ذي السلطان', '((اللَّهُمَّ أَنْتَ عَضُدِي، وَأَنْتَ نَصِيرِي، بِكَ أَحُولُ وَبِكَ أَصُولُ، وَبِكَ أُقاتِلُ)).', NULL, 1, NULL, 'دعاء لقاء العدو و ذي السلطان'),
('دعاء لقاء العدو و ذي السلطان', '((حَسْبُنا اللَّهُ وَنِعْمَ الْوَكِيلُ)).', NULL, 1, NULL, 'دعاء لقاء العدو و ذي السلطان'),
('دعاء لقاء العدو و ذي السلطان', '((اللَّهُمَّ ربَّ السَّمَوَاتِ السَّبْعِ، وَرَبَّ الْعَرْشِ الْعَظِيمِ، كُنْ لِي جَاراً مِنْ فُلاَنِ بْنِ فُلاَنٍ، وَأَحْزَابِهِ مِنْ خَلاَئِقِكَ، أَنْ يَفْرُطَ عَلَيَّ أَحَدٌ مِنْهُمْ أَوْ يَطْغَى، عَزَّ جَارُكَ، وَجَلَّ ثَنَاؤُكَ، وَلاَ إِلَهَ إِلاَّ أَنْتَ)).', NULL, 1, NULL, 'دعاء من خاف ظلم السلطان'),
('دعاء لقاء العدو و ذي السلطان', '((اللَّهُ أَكْبَرُ، اللَّهُ أَعَزُّ مِنْ خَلْقِهِ جَمِيعاً، اللَّهُ أَعَزُّ مِمَّا أَخَافُ وَأَحْذَرُ، أَعُوذُ بِاللَّهِ الَّذِي لاَ إِلَهَ إِلاَّ هُوَ، الْمُمْسِكِ السَّمَوَاتِ السَّبْعِ أَنْ يَقَعْنَ عَلَى الْأَرْضِ إِلاَّ بِإِذْنِهِ، مِنْ شَرِّ عَبْدِكَ فُلاَنٍ، وَجُنُودِهِ وَأَتْبَاعِهِ وَأَشْيَاعِهِ، مِنْ الْجِنِّ وَالإِنْسِ، اللَّهُمَّ كُنْ لِي جَاراً مِنْ شَرِّهِمْ، جَلَّ ثَنَاؤُكَ وَعَزَّ جَارُكَ، وَتَبَارَكَ اسْمُكَ، وَلاَ إِلَهَ غَيْرُكَ)) (ثلاثَ مرَّاتٍ).', NULL, 1, NULL, 'دعاء من خاف ظلم السلطان'),
('الدعاء على العدو', '((اللَّهُمَّ مُنْزِلَ الْكِتَابِ، سَرِيعَ الْحِسَابِ، اهْزِمِ الأَحْزَابَ، اللَّهُمَّ اهزِمْهُمْ وَزَلْزِلْهُمْ)).', NULL, 1, NULL, 'الدعاء على العدو'),
('ما يقول من خاف قوما', '((اللَّهُمَّ اكْفِنِيهِمْ بِمَا شِئْتَ)).', NULL, 1, NULL, 'ما يقول من خاف قوما - الخوف الرهبة '),
('دعاء من أصابه وسوسة في الإيمان', '((يَسْتَعِيذُ بِاللَّهِ و يَنْتَهِي عَمَّا شك فِيهِ)).', NULL, 1, NULL, 'دعاء من أصابه وسوسة في الإيمان'),
('دعاء من أصابه وسوسة في الإيمان', '((يَقُولُ: ((آمَنْتُ بِاللَّهِ وَرُسُلِهِ)).', NULL, 1, NULL, 'دعاء من أصابه وسوسة في الإيمان'),
('دعاء من أصابه وسوسة في الإيمان', '((يَقْرَأُ قَوْلَهُ تَعَالَى: ﴿هُوَ الْأوَّلُ وَالْآخِرُ وَالظّاهِرُ وَالْباطِنُ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ﴾)).', NULL, 1, NULL, 'دعاء من أصابه وسوسة في الإيمان'),
('دعاء قضاء الدين', '((اللَّهُمَّ اكْفِنِي بِحَلاَلِكَ عَنْ حَرَامِكَ، وَأَغْنِنِي بِفَضْلِكِ عَمَّنْ سِوَاكَ)).', NULL, 1, NULL, 'دعاء قضاء الدين - سداد الدين الديون'),
('دعاء قضاء الدين', '((اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنَ الْهَمِّ وَالْحَزَنِ، وَالْعَجْزِ وَالْكَسَلِ، وَالْبُخْلِ وَالْجُبْنِ، وَضَلَعِ الدَّيْنِ وَغَلَبَةِ الرِّجَالِ)).', NULL, 1, NULL, 'دعاء قضاء الدين - سداد الدين الديون'),
('دعاء الوسوسة في الصلاة و القراءة', '((أَعُوذُ بِاللَّهِ مِنَ الشَّيطَانِ الرَّجِيمِ، وَاتْفُلْ عَلَى يَسَارِكَ (ثلاثاً))).', NULL, 1, NULL, 'دعاء الوسوسة في الصلاة و القراءة - الشيطان الصلاة'),
('دعاء من استصعب عليه أمر', '((اللَّهُمَّ لاَ سَهْلَ إِلاَّ مَا جَعَلْتَهُ سَهْلاً، وَأَنْتَ تَجْعَلُ الْحَزْنَ إِذَا شِئْتَ سَهْلاً)).', NULL, 1, NULL, 'دعاء من استصعب عليه أمر'),
('ما يقول ويفعل من أذنب ذنبا', '((مَا مِنْ عَبْدٍ يُذنِبُ ذَنْباً فَيُحْسِنُ الطُّهُورَ، ثُمَّ يَقُومُ فَيُصَلِّي رَكْعَتَيْنِ، ثُمَّ يَسْتَغْفِرُ اللَّهَ إِلاَّ غَفَرَ اللَّهُ لَهُ)).', NULL, 1, NULL, 'ما يقول ويفعل من أذنب ذنبا - الذنب المعصية الاستغفار الإستغفار'),
('دعاء طرد الشيطان و وساوسه', '((الْاسْتِعَاذَةُ بِاللَّهِ مِنْهُ)).', NULL, 1, NULL, 'دعاء طرد الشيطان و وساوسه'),
('دعاء طرد الشيطان و وساوسه', '((الْأَذَانُ)).', NULL, 1, NULL, 'دعاء طرد الشيطان و وساوسه'),
('دعاء طرد الشيطان و وساوسه', '((الْأَذْكَارُ وَقِرَاءَةُ الْقُرْآنِ)).', NULL, 1, NULL, 'دعاء طرد الشيطان و وساوسه'),
('الدعاء حينما يقع ما لا يرضاه أو غلب على أمره', '((قَدَرُ اللَّهُ وَمَا شَاءَ فَعَلَ)).', NULL, 1, NULL, 'الدعاء حينما يقع ما لا يرضاه أو غلب على أمره'),
('ﺗﻬنئة المولود له وجوابه', '((بَارَكَ اللَّهُ لَكَ فِي الْمَوْهُوبِ لَكَ، وَشَكَرْتَ الْوَاهِبَ، وَبَلَغَ أَشُدَّهُ، وَرُزِقْتَ بِرَّهُ)). وَيَرُدُّ عَلَيْهِ الْمُهَــــــنَّأُ فَيَقُولُ: ((بَارَكَ اللَّهُ لَكَ وَبَارَكَ عَلَيْكَ، وَجَزَاكَ اللَّهُ خَيْراً، وَرَزَقَكَ اللَّهُ مِثْلَهُ، وَأَجْزَلَ ثَوَابَكَ)).', NULL, 1, NULL, 'ﺗﻬنئة المولود له وجوابه - التهنئة الطفل الولادة '),
('ما يعوذ به الأولاد', 'كَانَ رَسُولُ اللَّهِ صلى الله عليه وسلم يُعَوِّذُ الحَسَنَ وَالحُسَينَ رضي الله عنهما ((أُعِيذُكُمَا بِكَلِمَاتِ اللَّهِ التَّامَّةِ مِنْ كُلِّ شَيْطَانٍ وَهَامَّةٍ، وَمِنْ كُلِّ عَيْنٍ لاَمَّةٍ)).', NULL, 1, NULL, 'ما يعوذ به الأولاد - رقية'),
('الدعاء للمريض في عيادته', '((لاَ بأْسَ طَهُورٌ إِنْ شَاءَ اللَّهُ)).', NULL, 1, NULL, 'الدعاء للمريض في عيادته - زيارة المريض'),
('الدعاء للمريض في عيادته', 'أَسْأَلُ اللَّهَ الْعَظيمَ رَبَّ الْعَرْشِ الْعَظِيمِ أَنْ يَشْفيَكَ', NULL, 7, NULL, 'الدعاء للمريض في عيادته - زيارة المريض'),
('فضل عيادة المريض', 'قَالَ النَّبِيُّ صلى الله عليه وسلم: ((إِذَا عَادَ الرَّجُلُ أَخَاهُ الْمُسْلِمَ مَشَى فِي خِرَافَةِ الْجَنَّةِ حَتَّى يَجْلِسَ، فَإِذَا جَلَسَ غَمَرَتْهُ الرَّحْمَةُ، فَإِنْ كَانَ غُدْوَةً صَلَّى عَلَيْهِ سَبْعُونَ أَلْفَ مَلَكٍ حَتَّى يُمْسِيَ، وَإِنْ كَانَ مَسَاءً صَلَّى عَلَيْهِ سَبْعُونَ أَلْفَ مَلَكٍ حَتَّى يُصْبِحَ)).', NULL, 1, NULL, 'فضل عيادة المريض - زيارة المريض'),
('دعاء المريض الذي يئس من حياته', '((اللَّهُمَّ اغْفِرْ لِي، وَارْحَمْنِي، وَأَلْحِقْنِي بِالرَّفِيقِ الْأَعْلَى)).', NULL, 1, NULL, 'دعاء المريض الذي يئس من حياته - زيارة المريض'),
('دعاء المريض الذي يئس من حياته', '((جَعَلَ النَّبِيُّ صلى الله عليه وسلم عِنْدَ مَوْتِهِ يُدْخِلُ يَدَيْهِ فِي الْمَاءِ فَيَمْسَحُ بِهِمَا وَجْهَهُ، وَيَقُولُ: لاَ إِلَهَ إِلاَّ اللَّهُ إِنَّ لِلْمَوْتِ سَكَرَاتٍ)).', NULL, 1, NULL, 'دعاء المريض الذي يئس من حياته - زيارة المريض');